example.programlisting:
	#include <qpid/messaging/Connection.h>
	#include <qpid/messaging/Message.h>
	#include <qpid/messaging/Receiver.h>
	#include <qpid/messaging/Sender.h>
	#include <qpid/messaging/Session.h>

	#include <iostream>

	using namespace qpid::messaging;

	int main(int argc, char** argv) {
	std::string broker = argc > 1 ? argv[1] : "localhost:5672";
	std::string address = argc > 2 ? argv[2] : "amq.topic";
	std::string connectionOptions = argc > 3 ? argv[3] : "";

	Connection connection(broker, connectionOptions);
	try {
        connection.open();  
        Session session = connection.createSession(); 

        Receiver receiver = session.createReceiver(address); 
        Sender sender = session.createSender(address); 

        sender.send(Message("Hello world!"));

        Message message = receiver.fetch(Duration::SECOND * 1); 
        std::cout << message.getContent() << std::endl;
        session.acknowledge(); 

        connection.close(); 
        return 0;
	} catch(const std::exception& error) {
        std::cerr << error.what() << std::endl;
        connection.close();
        return 1;
	}
	}

import sys from qpid.messaging import *
broker = "localhost:5672" if len(sys.argv)<2 else sys.argv[1] address = "amq.topic" if len(sys.argv)<3 else sys.argv[2]
connection = Connection(broker)
try: connection.open() session = connection.session()
sender = session.sender(address) receiver = session.receiver(address)
sender.send(Message("Hello world!"));
message = receiver.fetch(timeout=1) print message.content session.acknowledge()
except MessagingError,m: print m finally: connection.close()

using System;
using Org.Apache.Qpid.Messaging;  

namespace Org.Apache.Qpid.Messaging {
class Program {
static void Main(string[] args) {
String broker = args.Length > 0 ? args[0] : "localhost:5672";
String address = args.Length > 1 ? args[1] : "amq.topic";

Connection connection = null;
try {
connection = new Connection(broker);
connection.Open();   
Session session = connection.CreateSession();   

Receiver receiver = session.CreateReceiver(address);   
Sender sender = session.CreateSender(address);   

sender.Send(new Message("Hello world!"));

Message message = new Message();
message = receiver.Fetch(DurationConstants.SECOND * 1);   
Console.WriteLine("{0}", message.GetContent());
session.Acknowledge();   

connection.Close();   
} catch (Exception e) {
Console.WriteLine("Exception {0}.", e);
if (null != connection)
connection.Close();
}
}
}
}

row.entry:
A nested list in which each binding is represented by
a map. The entries of the map for a binding contain
the fields that describe an AMQP 0-10 binding. Here is
the format for x-bindings:


[
{
exchange: <exchange>,
queue: <queue>,
key: <key>,
arguments: {
<key_1>: <value_1>,
...,
<key_n>: <value_n> }
},
...
]

